import React, {useEffect, useState, useCallback} from 'react';
import io from 'socket.io-client';

let socket;

const Chat = () => {

    const port = 'localhost:3000'
    const [input, updateInput] = useState("");
    const [text, updateText] = useState([]);
    const [ownText, updateOwnText] = useState("");

    useEffect( ()=> {
       socket = io(port)
       console.log(socket)

    },[port]);

    const addMessage = useCallback((data) => {
        let copyText = [...text];
        updateText(copyText, data);

    }, [text]);

    useEffect( () => {
        socket = io(port);
        socket.on('message', (data) => {
            addMessage(data);
            console.log(data)
        })
    }, [addMessage])

    const onChange = (e) => {
        let value = e.target.value;
        updateInput(value);
    }

    const onSubmit = (e) => {
        e.preventDefault();

        socket = io(port)
        socket.emit('new_message', input);
        addOwnMessage(input);
        updateInput("");
    }

    const addOwnMessage = (data) => {
        let copyText = [...text];
        updateText(copyText, data);
        console.log(copyText)
    };



    return <>
             <h1>Chat</h1>
             <form onSubmit = {onSubmit}>
                 <input onChange={onChange}></input>
                 <button></button>
             </form>
             {text.map(data => {
                 console.log(data)
                 return <p key={data}>{data}</p>
             })
             } 
           </>
}

export default Chat;
