import React, {useEffect, useState} from 'react';
import io from 'socket.io-client';

let socket;

const Chat = () => {

    const port = 'localhost:3000'
    const [input, updateInput] = useState("");
    const [text, updateText] = useState([]);

    useEffect( ()=> {
       socket = io(port)
       console.log(socket)

    },[port]);

    useEffect( () => {
        socket = io(port);
        socket.on('message', (data) => {
            addMessage(data);
        })
    })

    const onChange = (e) => {
        let value = e.target.value;
        updateInput(value);
    }

    const onSubmit = (e) => {
        e.preventDefault();

        socket = io(port)
        socket.emit('new_message', input);
        addMessage(input);
        updateInput("");
    }

    const addMessage = (data) => {
        updateText(...text, data)
    }

    console.log(text)
    return <>
             <h1>Chat</h1>
             <form onSubmit = {onSubmit}>
                 <input onChange={onChange}></input>
                 <button></button>
             </form>
             {text.map(data => {
                 return <p key={data}>data</p>
             })
             }
           </>
}

export default Chat;
