import React, {useEffect, useState} from 'react';
import io from 'socket.io-client';

let socket = io('localhost:3000');

const Chat = ({location}) => {

    const [input, updateInput] = useState("");
    const [text, updateText] = useState([]);
    let name = location.state.user;

    useEffect( () => {
        //console.log("USERNAME", location.state.user)
        socket.on('message', (data) => {
            let message = data;
            let copyMessage = [...text];		
            updateText([...copyMessage, message]);
            console.log(data)
        })
    }, [text])

    const onChange = (e) => {
        let value = e.target.value;
        updateInput(value);
    }

    const onSubmit = (e) => {
        e.preventDefault();

        //socket.emit('new_message', input);
        socket.emit("new_message",{
            username: name,
            content: input,
        })
        updateInput("");
        let message = {username: name, content: input};
        let copyMessage = [...text];	
        //copyMessage.splice(0, 1);	
        updateText([...copyMessage, message]);

    }


    return <>
             <h1>Chat</h1>
             <p>Hej, {name}</p>
             <form onSubmit = {onSubmit}>
                 <input onChange={onChange} value={input}></input>
                 <button></button>
             </form>
            {text.map(data => {
                 //console.log(data)
                 return <div key={data.id}>
                            <p>{data.username}</p>
                            <p>{data.content}</p>
                        </div>
             })
             } 
           </>
}

export default Chat;
